// src/screens/HasilPositif.js
import { useLocation, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';

export default function HasilPositif() {
  const location = useLocation();
  const navigate = useNavigate();

  const {
    nama = 'Nama Tidak Diketahui',
    probabilitas = 0.0,
    tanggal,
  } = location.state || {};

  const tanggalFormatted = tanggal
    ? new Date(tanggal).toLocaleDateString('id-ID', {
        day: '2-digit',
        month: 'long',
        year: 'numeric',
      })
    : '-';

  const handleHubungiKami = () => {
    const nomorWA = '6281274489306';
    const pesan = 'Halo, saya ingin konsultasi mengenai hasil screening TBC saya.';
    const url = `https://wa.me/${nomorWA}?text=${encodeURIComponent(pesan)}`;
    window.open(url, '_blank');
  };

  const handleKembali = () => {
    navigate('/');
  };

  return (
    <div className="d-flex flex-column min-vh-100">
      <Header />

      <div className="container my-5 flex-grow-1 text-center">
        <h2 className="fw-bold text-danger">SCREENING TBC</h2>
        <p className="text-muted">
          Segera periksa diri Anda ke fasilitas kesehatan terdekat. TBC bisa disembuhkan!
        </p>

        <div
          className="card mx-auto p-4"
          style={{ maxWidth: '500px', backgroundColor: '#ffebee' }}
        >
          <img
            src="/assets/images/Sakit.png"
            alt="Sakit"
            className="img-fluid mb-3"
            style={{ maxWidth: 140 }}
          />

          <div className="bg-white shadow-sm rounded p-3 mb-3">
            <h5 className="mb-1 fw-bold">{nama}</h5>
            <small className="text-muted">{tanggalFormatted}</small>
          </div>

          <h5 className="text-danger fw-bold mb-2">Anda Terduga TBC</h5>
          <p className="mb-0">Probabilitas: {(probabilitas * 100).toFixed(2)}%</p>
        </div>

        <p className="mt-4 text-muted">
          Segera kunjungi Puskesmas atau fasilitas kesehatan terdekat untuk pemeriksaan lebih lanjut.
        </p>

        <div className="d-grid gap-3 mt-4 col-10 col-md-6 mx-auto">
          <button className="btn btn-success" onClick={handleHubungiKami}>
            Hubungi Kami
          </button>
          <button className="btn btn-outline-secondary" onClick={handleKembali}>
            Kembali ke Halaman Awal
          </button>
        </div>
      </div>

      <Footer />
    </div>
  );
}
