import { useState } from 'react';
import {
    Alert,
    Platform,
    ScrollView,
    StyleSheet,
    Text,
    TextInput,
    TouchableOpacity,
    View,
} from 'react-native';
import Footer from '../components/Footer';
import Header from '../components/Header';

export default function InformasiAwal({ navigation }) {
    const [nama, setNama] = useState('');
    const [nik, setNik] = useState('');

    const showAlert = (title, message) => {
        if (Platform.OS === 'web') {
            window.alert(`${title}\n\n${message}`);
        } else {
            Alert.alert(title, message);
        }
    };

    const handleNext = () => {
        console.log('Tombol Selanjutnya ditekan');

        if (!nama.trim()) {
            showAlert('Validasi Gagal', 'Nama lengkap harus diisi.');
            return;
        }

        if (nik.length < 16) {
            showAlert('Validasi Gagal', 'NIK harus minimal 16 digit.');
            return;
        }

        // --- Perubahan di sini: Kirim nama dan nik sebagai parameter ---
        navigation.navigate('IdentitasAwal', {
            namaLengkap: nama, // Kirim nama dengan key yang jelas
            nomorNIK: nik,     // Kirim NIK dengan key yang jelas
        });
    };

    return (
        <View style={styles.screen}>
            <Header />
            <ScrollView contentContainerStyle={styles.container}>
                <Text style={styles.header}>SCREENING TBC</Text>
                <Text style={styles.subHeader}>
                    TBC itu bisa diobati kok, Telaten minum obat dan rutin kontrol. Jangan takut untuk memeriksakan diri!
                </Text>

                <View style={styles.card}>
                    <Text style={styles.cardTitle}>Identitas Awal</Text>

                    <TextInput
                        style={styles.input}
                        placeholder="Nama Lengkap"
                        value={nama}
                        onChangeText={setNama}
                    />

                    <TextInput
                        style={styles.input}
                        placeholder="Nomor Induk Kependudukan (NIK)"
                        value={nik}
                        onChangeText={(text) => setNik(text.replace(/[^0-9]/g, ''))}
                        keyboardType="numeric"
                        maxLength={16}
                    />

                    <TouchableOpacity style={styles.button} onPress={handleNext}>
                        <Text style={styles.buttonText}>Selanjutnya</Text>
                    </TouchableOpacity>
                </View>
            </ScrollView>
            <Footer />
        </View>
    );
}

const styles = StyleSheet.create({
    screen: {
        flex: 1,
        backgroundColor: '#fff',
    },
    container: {
        padding: 20,
        flexGrow: 1,
    },
    header: {
        fontSize: 22,
        fontWeight: 'bold',
        color: 'red',
        textAlign: 'center',
        marginBottom: 8,
    },
    subHeader: {
        fontSize: 14,
        textAlign: 'center',
        marginBottom: 20,
    },
    card: {
        borderColor: 'red',
        borderWidth: 2,
        borderRadius: 8,
        padding: 20,
        backgroundColor: '#fdfdfd',
        shadowColor: '#000',
        shadowOpacity: 0.1,
        shadowRadius: 4,
    },
    cardTitle: {
        fontSize: 18,
        fontWeight: 'bold',
        marginBottom: 16,
        color: 'red',
    },
    input: {
        borderWidth: 1,
        borderColor: '#ccc',
        borderRadius: 6,
        padding: 12,
        marginBottom: 16,
    },
    button: {
        backgroundColor: 'red',
        padding: 14,
        borderRadius: 6,
        alignItems: 'center',
    },
    buttonText: {
        color: '#fff',
        fontWeight: 'bold',
    },
});
