import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';

export default function DetailScreening() {
  const location = useLocation();
  const navigate = useNavigate();
  const { item } = location.state || {};
  const [allowed, setAllowed] = useState(false);
  const [checking, setChecking] = useState(true);

  const pertanyaanList = [
    'Batuk Lebih Dari 2 Minggu',
    'Demam',
    'Berkeringat Malam Hari Tanpa Aktivitas',
    'Sesak Nafas',
    'Nyeri Dada',
    'Ada benjolan di leher/bawah rahang/bawah telinga/ketiak',
    'Batuk berdarah',
    'Batuk kurang dari 2 minggu',
    'Nafsu makan turun (Atau Hilang Nafsu Makan Selama Berhari-hari)',
    'Mudah lelah (Atau Sering Kecapekan Tanpa Aktivitas Fisik Yang Berarti)',
    'Berat badan turun (Turun Drastis Selama Berhari-hari Bukan Karena Diet)',
    'Anggota keluarga serumah ada yang sakit TBC?',
    'Pernah berada satu ruangan dengan penderita TBC (di kantor, tempat kerja/kelas/kamar/asrama/panti/barak, dll)?',
    'Apakah pernah tinggal serumah minimal satu malam atau sering tinggal serumah pada siang hari dengan orang yang sakit TBC?',
    'Pernah Berobat TBC tuntas',
    'Pernah berobat TBC tapi tidak tuntas',
    'Punya riwayat diabetes melitus/kencing manis',
    'Orang dengan HIV',
    'Ibu hamil',
    'Merokok',
    'Usia 0-14 Tahun',
    'Lansia (Diatas 60 Tahun)',
  ];

  const convertJawaban = (val) => {
    if (val === 'ya' || val === 1 || val === '1' || val === true) return 'Ya';
    if (val === 'tidak' || val === 0 || val === '0' || val === false) return 'Tidak';
    return 'Tidak tersedia';
  };

  // ✅ Perbaikan logika parsing jawaban yang lebih robust
  const jawabanArray = (() => {
    const raw = item?.jawaban;
    
    try {
      // Jika null atau undefined
      if (!raw) return [];

      // Jika sudah array
      if (Array.isArray(raw)) return raw;

      // Jika string JSON
      if (typeof raw === 'string') {
        const trimmed = raw.trim();
        
        // Coba parse sebagai JSON array
        if (trimmed.startsWith('[') && trimmed.endsWith(']')) {
          return JSON.parse(trimmed);
        }
        
        // Coba parse sebagai comma-separated values
        if (trimmed.includes(',')) {
          return trimmed.split(',').map(val => {
            const clean = val.trim().replace(/["']/g, '');
            // Convert string numbers to numbers
            if (clean === '1' || clean === 'true') return 1;
            if (clean === '0' || clean === 'false') return 0;
            return clean.toLowerCase();
          });
        }
        
        // Single value
        const clean = trimmed.replace(/["']/g, '');
        if (clean === '1' || clean === 'true') return [1];
        if (clean === '0' || clean === 'false') return [0];
        return [clean.toLowerCase()];
      }

      // Jika object, ambil values
      if (typeof raw === 'object' && raw !== null) {
        return Object.values(raw);
      }

      return [];
    } catch (error) {
      console.error('Error parsing jawaban:', error);
      return [];
    }
  })();

  // ✅ Perbaikan parsing probabilitas
  const parsedProbabilitas = (() => {
    const prob = item?.probabilitas;
    if (prob === null || prob === undefined) return null;
    
    const parsed = parseFloat(prob);
    return isNaN(parsed) ? null : parsed;
  })();

  // ✅ Perbaikan format tanggal
  const formatTanggal = (tanggal) => {
    if (!tanggal) return 'Tidak tersedia';
    
    try {
      const date = new Date(tanggal);
      if (isNaN(date.getTime())) return 'Format tanggal tidak valid';
      
      return date.toLocaleDateString('id-ID', {
        day: '2-digit',
        month: 'long',
        year: 'numeric',
      });
    } catch (error) {
      return 'Format tanggal tidak valid';
    }
  };

  useEffect(() => {
    try {
      const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
      
      if (currentUser?.role === 'admin' || currentUser?.user_id === item?.user_id) {
        setAllowed(true);
      }
    } catch (error) {
      console.error('Error parsing user data:', error);
    } finally {
      setChecking(false);
    }
  }, [item?.user_id]);

  if (checking) {
    return (
      <div className="d-flex flex-column min-vh-100">
        <Header />
        <div className="container text-center mt-5 flex-grow-1">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <h5 className="mt-3">Mengecek izin akses...</h5>
        </div>
        <Footer />
      </div>
    );
  }

  if (!allowed) {
    return (
      <div className="d-flex flex-column min-vh-100">
        <Header />
        <div className="container text-center mt-5 flex-grow-1">
          <div className="alert alert-danger">
            ❌ Anda tidak memiliki izin untuk melihat detail screening ini.
          </div>
          <button className="btn btn-secondary" onClick={() => navigate(-1)}>
            🔙 Kembali
          </button>
        </div>
        <Footer />
      </div>
    );
  }

  // ✅ Validasi data item
  if (!item) {
    return (
      <div className="d-flex flex-column min-vh-100">
        <Header />
        <div className="container text-center mt-5 flex-grow-1">
          <div className="alert alert-warning">
            ⚠️ Data screening tidak ditemukan.
          </div>
          <button className="btn btn-secondary" onClick={() => navigate(-1)}>
            🔙 Kembali
          </button>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="d-flex flex-column min-vh-100">
      <Header />
      <div className="container my-4 flex-grow-1">
        <div className="row">
          <div className="col-12">
            <div className="card">
              <div className="card-header">
                <h2 className="mb-0">📝 Detail Screening</h2>
              </div>
              <div className="card-body">
                {/* Informasi Umum */}
                <div className="row mb-4">
                  <div className="col-md-6">
                    <p><strong>👤 Nama:</strong> {item?.nama_pasien || 'Tidak tersedia'}</p>
                    <p><strong>📅 Tanggal:</strong> {formatTanggal(item?.tanggal_screening)}</p>
                  </div>
                  <div className="col-md-6">
                    {parsedProbabilitas !== null && (
                      <p>
                        <strong>📈 Probabilitas:</strong>{' '}
                        <span className={`fw-bold ${parsedProbabilitas > 0.5 ? 'text-danger' : 'text-success'}`}>
                          {(parsedProbabilitas * 100).toFixed(2)}%
                        </span>
                      </p>
                    )}
                    
                    {item?.hasil && (
                      <p>
                        <strong>⚠️ Hasil:</strong>{' '}
                        <span className={`fw-bold badge ${
                          item.hasil.toLowerCase().includes('positif') 
                            ? 'bg-danger' 
                            : 'bg-success'
                        }`}>
                          {item.hasil}
                        </span>
                      </p>
                    )}
                  </div>
                </div>

                {/* Jawaban */}
                <h5 className="mt-4 mb-3">🗒️ Jawaban:</h5>
                <div className="table-responsive">
                  <table className="table table-hover">
                    <thead className="table-light">
                      <tr>
                        <th style={{ width: '5%' }}>No</th>
                        <th style={{ width: '70%' }}>Pertanyaan</th>
                        <th style={{ width: '25%' }}>Jawaban</th>
                      </tr>
                    </thead>
                    <tbody>
                      {pertanyaanList.map((pertanyaan, index) => {
                        const jawabanTeks = convertJawaban(jawabanArray[index]);
                        let badgeClass = 'bg-secondary';
                        if (jawabanTeks === 'Ya') badgeClass = 'bg-success';
                        else if (jawabanTeks === 'Tidak') badgeClass = 'bg-danger';

                        return (
                          <tr key={index}>
                            <td className="text-center">{index + 1}</td>
                            <td>{pertanyaan}</td>
                            <td>
                              <span className={`badge rounded-pill ${badgeClass}`}>
                                {jawabanTeks}
                              </span>
                            </td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </table>
                </div>

                {/* Summary */}
                <div className="row mt-4">
                  <div className="col-md-4">
                    <div className="card bg-light">
                      <div className="card-body text-center">
                        <h6 className="card-title">Jawaban "Ya"</h6>
                        <h4 className="text-success">
                          {jawabanArray.filter(j => convertJawaban(j) === 'Ya').length}
                        </h4>
                      </div>
                    </div>
                  </div>
                  <div className="col-md-4">
                    <div className="card bg-light">
                      <div className="card-body text-center">
                        <h6 className="card-title">Jawaban "Tidak"</h6>
                        <h4 className="text-danger">
                          {jawabanArray.filter(j => convertJawaban(j) === 'Tidak').length}
                        </h4>
                      </div>
                    </div>
                  </div>
                  <div className="col-md-4">
                    <div className="card bg-light">
                      <div className="card-body text-center">
                        <h6 className="card-title">Tidak Tersedia</h6>
                        <h4 className="text-secondary">
                          {jawabanArray.filter(j => convertJawaban(j) === 'Tidak tersedia').length}
                        </h4>
                      </div>
                    </div>
                  </div>
                </div>

                <div className="text-center mt-4">
                  <button className="btn btn-primary" onClick={() => navigate(-1)}>
                    🔙 Kembali
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}
