import AsyncStorage from '@react-native-async-storage/async-storage';
import { useEffect, useState } from 'react';
import {
  Alert,
  Platform,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from 'react-native';
import Footer from '../components/Footer';
import Header from '../components/Header';

export default function IdentitasAwal({ navigation, route }) {
  const { namaLengkap, nomorNIK } = route.params || {};

  const [gender, setGender] = useState('');
  const [phone, setPhone] = useState('');
  const [age, setAge] = useState('');
  const [address, setAddress] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkLogin = async () => {
      const userId = await AsyncStorage.getItem('user_id');
      if (!userId) {
        Alert.alert(
          'Akses Ditolak',
          'Silakan login terlebih dahulu sebelum melakukan screening.',
          [
            {
              text: 'OK',
              onPress: () => navigation.replace('LoginScreen', {
                redirectTo: 'InformasiAwal',
                params: {}, // Optional jika InformasiAwal tidak butuh param
              }),
            },
          ]
        );
      } else {
        setLoading(false);
      }
    };
    checkLogin();
  }, []);

  const showAlert = (title, message) => {
    if (Platform.OS === 'web') {
      window.alert(`${title}\n\n${message}`);
    } else {
      Alert.alert(title, message);
    }
  };

  const handleNext = () => {
    if (!gender) {
      showAlert('Validasi Gagal', 'Jenis kelamin harus dipilih.');
      return;
    }
    if (!phone.trim()) {
      showAlert('Validasi Gagal', 'No Telepon harus diisi.');
      return;
    }
    if (!age.trim()) {
      showAlert('Validasi Gagal', 'Usia harus diisi.');
      return;
    }
    if (!address.trim()) {
      showAlert('Validasi Gagal', 'Alamat domisili harus diisi.');
      return;
    }

    const identitasLengkap = {
      nama: namaLengkap,
      nik: nomorNIK,
      jenisKelamin: gender === 'L' ? 'Laki-laki' : 'Perempuan',
      telepon: phone,
      usia: age,
      alamat: address,
    };

    navigation.navigate('Screening', { identitas: identitasLengkap });
  };

  if (loading) return null;

  return (
    <SafeAreaView style={styles.safeArea}>
      <Header />
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.formContainer}>
          <Text style={styles.header}>SCREENING TBC</Text>
          <Text style={styles.subHeader}>
            TBC itu bisa diobati kok, Telaten minum obat dan rutin kontrol. Jangan takut untuk memeriksakan diri!
          </Text>

          <View style={styles.card}>
            <Text style={styles.cardTitle}>Identitas Awal</Text>

            <View style={styles.genderContainer}>
              <TouchableOpacity
                style={[styles.genderButton, gender === 'L' && styles.genderSelected]}
                onPress={() => setGender('L')}
              >
                <Text style={gender === 'L' ? styles.genderTextSelected : styles.genderText}>Laki - laki</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={[styles.genderButton, gender === 'P' && styles.genderSelected]}
                onPress={() => setGender('P')}
              >
                <Text style={gender === 'P' ? styles.genderTextSelected : styles.genderText}>Perempuan</Text>
              </TouchableOpacity>
            </View>

            <TextInput
              style={styles.input}
              placeholder="No Telepon/HP"
              keyboardType="phone-pad"
              value={phone}
              onChangeText={setPhone}
            />
            <TextInput
              style={styles.input}
              placeholder="Usia (Tahun)"
              keyboardType="numeric"
              value={age}
              onChangeText={setAge}
            />
            <TextInput
              style={styles.input}
              placeholder="Alamat Domisili"
              value={address}
              onChangeText={setAddress}
            />

            <View style={styles.buttonGroup}>
              <TouchableOpacity
                style={[styles.button, styles.secondaryButton]}
                onPress={() => navigation.goBack()}
              >
                <Text style={styles.secondaryButtonText}>Sebelumnya</Text>
              </TouchableOpacity>
              <TouchableOpacity style={styles.button} onPress={handleNext}>
                <Text style={styles.buttonText}>Selanjutnya</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
        <Footer />
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  safeArea: {
    flex: 1,
    backgroundColor: '#fff',
  },
  scrollContent: {
    flexGrow: 1,
    justifyContent: 'space-between',
    minHeight: '100%',
  },
  formContainer: {
    padding: 20,
  },
  header: {
    fontSize: 22,
    fontWeight: 'bold',
    color: 'red',
    textAlign: 'center',
    marginBottom: 8,
  },
  subHeader: {
    fontSize: 14,
    textAlign: 'center',
    marginBottom: 20,
  },
  card: {
    borderColor: 'red',
    borderWidth: 2,
    borderRadius: 8,
    padding: 20,
    backgroundColor: '#fdfdfd',
  },
  cardTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 16,
    color: 'red',
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 6,
    padding: 12,
    marginBottom: 16,
  },
  genderContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 16,
  },
  genderButton: {
    flex: 1,
    borderWidth: 1,
    borderColor: '#ccc',
    padding: 12,
    marginHorizontal: 4,
    borderRadius: 6,
    backgroundColor: '#fff',
    alignItems: 'center',
  },
  genderSelected: {
    borderColor: 'red',
    backgroundColor: '#ffe6e6',
  },
  genderText: {
    color: '#555',
  },
  genderTextSelected: {
    color: 'red',
    fontWeight: 'bold',
  },
  buttonGroup: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 12,
  },
  button: {
    backgroundColor: 'red',
    padding: 14,
    borderRadius: 6,
    alignItems: 'center',
    flex: 1,
    marginHorizontal: 4,
  },
  secondaryButton: {
    backgroundColor: '#eee',
  },
  secondaryButtonText: {
    color: '#333',
    fontWeight: 'bold',
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
  },
});
