import { useEffect, useState } from 'react';
import {
  Alert,
  Platform,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import Footer from '../components/Footer';
import Header from '../components/Header';

export default function DataScreening({ navigation }) {
  const [data, setData] = useState([]);
  const [userInfo, setUserInfo] = useState(null);

  // Ambil data riwayat screening dari backend
  const fetchScreeningData = async () => {
    try {
      const userData = await AsyncStorage.getItem('user');
      const parsedUser = userData ? JSON.parse(userData) : null;
      setUserInfo(parsedUser);

      if (!parsedUser) return;

      const response = await fetch(`http://localhost:5000/riwayat-screening?user_id=${parsedUser.user_id}&role=${parsedUser.role}`);
      const result = await response.json();

      if (response.ok) {
        setData(result);
        await AsyncStorage.setItem('screening_results', JSON.stringify(result)); // cache untuk offline jika ingin
      } else {
        console.error('Gagal memuat data:', result);
      }
    } catch (error) {
      console.error('Gagal mengambil data dari backend:', error);
    }
  };

  useEffect(() => {
    const unsubscribe = navigation.addListener('focus', fetchScreeningData);
    return unsubscribe;
  }, [navigation]);

  const showAlert = (title, message, onConfirm) => {
    if (Platform.OS === 'web') {
      const confirmed = window.confirm(message);
      if (confirmed && onConfirm) onConfirm();
    } else {
      Alert.alert(title, message, [
        { text: 'Batal', style: 'cancel' },
        { text: 'Hapus', style: 'destructive', onPress: onConfirm },
      ]);
    }
  };

  // â—Hapus data hanya secara lokal karena backend belum mendukung hapus
  const handleDelete = async (index) => {
    const deletedItem = data[index];
    const allData = JSON.parse(await AsyncStorage.getItem('screening_results')) || [];

    const updatedAll = allData.filter((item) => {
      return item !== deletedItem;
    });

    await AsyncStorage.setItem('screening_results', JSON.stringify(updatedAll));
    const updatedData = [...data];
    updatedData.splice(index, 1);
    setData(updatedData);
  };

  const handleDetail = (item) => {
    navigation.navigate('DetailScreening', { item });
  };

  const handleBackToMenu = () => {
    navigation.navigate('MainMenu');
  };

  return (
    <SafeAreaView style={styles.container}>
      <Header />
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.contentWrapper}>
          <Text style={styles.loginInfo}>
            Login sebagai: {userInfo?.username || 'Belum Login'}
          </Text>

          {data.length === 0 ? (
            <Text style={styles.emptyText}>Belum ada data screening.</Text>
          ) : (
            data.map((item, index) => (
              <View key={index} style={styles.card}>
                <Text style={styles.name}>{item.nama_pasien}</Text>
                <Text style={styles.detail}>
                  Tanggal:{' '}
                  {item.tanggal_screening
                    ? new Date(item.tanggal_screening).toLocaleDateString('id-ID', {
                        day: '2-digit',
                        month: 'long',
                        year: 'numeric',
                      })
                    : 'Tidak tersedia'}
                </Text>
                <Text style={styles.detail}>
                  Probabilitas: {(item.probabilitas * 100 || 0).toFixed(2)}%
                </Text>
                <Text
                  style={[
                    styles.result,
                    { color: item.hasil === 'Positif' ? 'red' : 'green' },
                  ]}
                >
                  Hasil: {item.hasil}
                </Text>
                <View style={styles.buttonGroup}>
                  <TouchableOpacity
                    style={styles.detailButton}
                    onPress={() => handleDetail(item)}
                  >
                    <Text style={styles.buttonText}>Lihat Detail</Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={styles.deleteButton}
                    onPress={() =>
                      showAlert('Konfirmasi', 'Yakin ingin menghapus data ini?', () =>
                        handleDelete(index)
                      )
                    }
                  >
                    <Text style={styles.buttonText}>Hapus</Text>
                  </TouchableOpacity>
                </View>
              </View>
            ))
          )}

          <TouchableOpacity style={styles.backButton} onPress={handleBackToMenu}>
            <Text style={styles.backButtonText}>ðŸ”™ Kembali ke Menu Utama</Text>
          </TouchableOpacity>
        </View>
        <Footer />
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#fff' },
  scrollContent: { flexGrow: 1, justifyContent: 'space-between' },
  contentWrapper: { padding: 16 },
  loginInfo: {
    fontWeight: 'bold',
    marginBottom: 10,
    color: '#444',
  },
  emptyText: {
    textAlign: 'center',
    marginTop: 20,
    color: '#888',
  },
  card: {
    backgroundColor: '#f2f2f2',
    borderRadius: 8,
    padding: 16,
    marginBottom: 12,
    elevation: 1,
  },
  name: {
    fontWeight: 'bold',
    fontSize: 16,
    marginBottom: 4,
  },
  detail: {
    fontSize: 14,
    color: '#333',
  },
  result: {
    fontSize: 14,
    fontWeight: 'bold',
    marginTop: 8,
  },
  buttonGroup: {
    flexDirection: 'row',
    marginTop: 10,
    justifyContent: 'space-between',
  },
  detailButton: {
    backgroundColor: '#2196F3',
    padding: 10,
    borderRadius: 6,
    flex: 1,
    marginRight: 8,
  },
  deleteButton: {
    backgroundColor: '#f44336',
    padding: 10,
    borderRadius: 6,
    flex: 1,
  },
  buttonText: {
    color: '#fff',
    textAlign: 'center',
    fontWeight: 'bold',
  },
  backButton: {
    marginTop: 20,
    backgroundColor: '#1976d2',
    padding: 12,
    borderRadius: 8,
    alignItems: 'center',
  },
  backButtonText: {
    color: '#fff',
    fontWeight: 'bold',
    fontSize: 16,
  },
});
