import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Footer from '../components/Footer';
import Header from '../components/Header';

export default function DataScreening() {
  const [data, setData] = useState([]);
  const [userInfo, setUserInfo] = useState(null);
  const navigate = useNavigate();

  const fetchScreeningData = async () => {
    try {
      const userData = localStorage.getItem('user');
      const parsedUser = userData ? JSON.parse(userData) : null;
      setUserInfo(parsedUser);

      if (!parsedUser) return;

      const response = await fetch(
        `http://localhost:5000/riwayat-screening?user_id=${parsedUser.user_id}&role=${parsedUser.role}`
      );
      const result = await response.json();

      if (response.ok) {
        setData(result);
        localStorage.setItem('screening_results', JSON.stringify(result));
      } else {
        console.error('Gagal memuat data:', result);
      }
    } catch (error) {
      console.error('Gagal mengambil data:', error);
    }
  };

  useEffect(() => {
    fetchScreeningData();
  }, []);

  const handleDelete = (index) => {
    const confirmDelete = window.confirm('Yakin ingin menghapus data ini?');
    if (!confirmDelete) return;

    const deletedItem = data[index];
    const allData = JSON.parse(localStorage.getItem('screening_results')) || [];
    const updatedAll = allData.filter((item) => item !== deletedItem);
    localStorage.setItem('screening_results', JSON.stringify(updatedAll));

    const updatedData = [...data];
    updatedData.splice(index, 1);
    setData(updatedData);
  };

  const handleDetail = (item) => {
    navigate(`/detail/${item.id}`, { state: { item } });
  };

  const handleBack = () => {
    navigate('/');
  };

  return (
    <div className="container mt-4 mb-5">
      <Header />
      <div className="text-muted mb-3">Login sebagai: <strong>{userInfo?.username || 'Belum Login'}</strong></div>

      {data.length === 0 ? (
        <div className="alert alert-info">Belum ada data screening.</div>
      ) : (
        data.map((item, index) => (
          <div key={index} className="card mb-3 shadow-sm">
            <div className="card-body">
              <h5 className="card-title">{item.nama_pasien}</h5>
              <p className="card-text mb-1">
                <strong>Tanggal:</strong>{' '}
                {item.tanggal_screening
                  ? new Date(item.tanggal_screening).toLocaleDateString('id-ID', {
                      day: '2-digit',
                      month: 'long',
                      year: 'numeric',
                    })
                  : 'Tidak tersedia'}
              </p>
              <p className="card-text mb-1">
                <strong>Probabilitas:</strong> {(item.probabilitas * 100).toFixed(2)}%
              </p>
              <p className="card-text">
                <strong>Hasil:</strong>{' '}
                <span style={{ color: item.hasil === 'Positif' ? 'red' : 'green' }}>
                  {item.hasil}
                </span>
              </p>
              <div className="d-flex gap-2">
                <button className="btn btn-primary" onClick={() => handleDetail(item)}>
                  Lihat Detail
                </button>
                <button className="btn btn-danger" onClick={() => handleDelete(index)}>
                  Hapus
                </button>
              </div>
            </div>
          </div>
        ))
      )}

      <button className="btn btn-secondary mt-3" onClick={handleBack}>
        ðŸ”™ Kembali ke Menu Utama
      </button>

      <Footer />
    </div>
  );
}
