import AsyncStorage from '@react-native-async-storage/async-storage';
import {
  Alert,
  Platform,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from 'react-native';
import Footer from '../components/Footer';
import Header from '../components/Header';
import { informasiLainnyaQuestions, keluhanQuestions } from './question';

export default function ReviewJawaban({ route, navigation }) {
  const { identitas = {}, screeningAnswers = {} } = route.params || {};

  const identitasData = [
    { label: 'Nama Lengkap', value: identitas.nama },
    { label: 'Nomor Induk Kependudukan (NIK)', value: identitas.nik },
    { label: 'Jenis Kelamin', value: identitas.jenisKelamin },
    { label: 'No Telepon', value: identitas.telepon },
    { label: 'Usia (Tahun)', value: identitas.usia },
    { label: 'Alamat Domisili', value: identitas.alamat },
  ];

  const keluhan = keluhanQuestions.map((q) => [q, screeningAnswers[q] || '-']);
  const informasiLainnya = informasiLainnyaQuestions.map((q) => [q, screeningAnswers[q] || '-']);

  const renderRow = (label, value, key) => (
    <View style={styles.row} key={key || label}>
      <Text style={styles.cell}>{label}</Text>
      <Text style={styles.cellValue}>{value}</Text>
    </View>
  );

  const showAlert = (title, message) => {
    if (Platform.OS === 'web') {
      alert(`${title}\n${message}`);
    } else {
      Alert.alert(title, message);
    }
  };

  const handleSubmit = async () => {
    try {
      const userRaw = await AsyncStorage.getItem('user');
      const user = JSON.parse(userRaw);

      if (!user || !user.username || !user.user_id) {
        showAlert('Anda belum login', 'Silakan login terlebih dahulu.');
        navigation.navigate('LoginScreen', {
          redirectTo: 'ReviewJawaban',
          params: route.params,
        });
        return;
      }

      const features = [...keluhan, ...informasiLainnya].map(([_, val]) => (val === 'Ya' ? 1 : 0));

      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ features }),
      });

      if (!response.ok) throw new Error('Gagal mendapatkan hasil screening.');

      const { prediction, probability } = await response.json();
      const hasil = prediction === 1 ? 'Positif' : 'Negatif';

      const gejala = {};
      [...keluhan, ...informasiLainnya].forEach(([q, val]) => {
        const key = q.toLowerCase().replace(/[^a-z0-9]+/g, '_');
        gejala[key] = val === 'Ya' ? 1 : 0;
      });

      const submitResponse = await fetch('http://localhost:5000/submit-screening', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          user_id: parseInt(user.user_id),
          nama_pasien: identitas.nama,
          nik: identitas.nik,
          jenis_kelamin: identitas.jenisKelamin,
          no_hp: identitas.telepon,
          usia: identitas.usia,
          alamat: identitas.alamat,
          hasil,
          catatan: '',
          gejala,
        }),
      });

      if (!submitResponse.ok) throw new Error('Gagal menyimpan data screening.');

      navigation.navigate(prediction === 1 ? 'HasilPositif' : 'HasilNegatif', {
        nama: identitas.nama,
        hasil,
        probabilitas: probability,
      });
    } catch (error) {
      console.error('ðŸ”¥ ERROR:', error);
      showAlert('Error', 'Terjadi kesalahan saat mengirim data ke server.');
    }
  };

  return (
    <SafeAreaView style={styles.safeArea}>
      <Header />
      <ScrollView contentContainerStyle={styles.scrollContent} showsVerticalScrollIndicator>
        <View style={styles.pageWrapper}>
          <View style={styles.formContainer}>
            <Text style={styles.title}>SCREENING TBC</Text>
            <Text style={styles.subtitle}>
              TBC itu bisa diobati kok, Telaten minum obat dan rutin kontrol. Jangan takut untuk memeriksakan diri!
            </Text>

            <View style={styles.card}>
              <Text style={styles.sectionTitle}>Informasi Yang Di Screening</Text>
              {identitasData.map(({ label, value }, i) => renderRow(label, value, `identitas-${i}`))}

              <Text style={styles.sectionTitle}>Keluhan Yang Dirasakan</Text>
              {keluhan.map(([label, value], i) =>
                renderRow(`${i + 1}. ${label}`, value, `keluhan-${i}`)
              )}

              <Text style={styles.sectionTitle}>Informasi Lainnya</Text>
              {informasiLainnya.map(([label, value], i) =>
                renderRow(`${i + keluhan.length + 1}. ${label}`, value, `info-${i}`)
              )}

              <View style={styles.buttonGroup}>
                <TouchableOpacity
                  style={[styles.button, styles.editButton]}
                  onPress={() => navigation.goBack()}
                >
                  <Text style={styles.editText}>Edit</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.button} onPress={handleSubmit}>
                  <Text style={styles.submitText}>Submit Data</Text>
                </TouchableOpacity>
              </View>
            </View>
          </View>

          <Footer />
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  safeArea: { flex: 1, backgroundColor: '#fff' },
  scrollContent: { flexGrow: 1 },
  pageWrapper: { minHeight: '100%', justifyContent: 'space-between' },
  formContainer: { padding: 20 },
  title: {
    fontSize: 22,
    fontWeight: 'bold',
    color: 'red',
    textAlign: 'center',
    marginBottom: 8,
  },
  subtitle: { fontSize: 14, textAlign: 'center', marginBottom: 20 },
  card: {
    backgroundColor: '#fff',
    borderRadius: 8,
    borderWidth: 1,
    borderColor: '#ddd',
    padding: 20,
  },
  sectionTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'red',
    marginVertical: 16,
  },
  row: {
    flexDirection: 'row',
    borderBottomWidth: 1,
    borderBottomColor: '#eee',
    paddingVertical: 6,
  },
  cell: { flex: 1, fontSize: 14, color: '#333' },
  cellValue: { flex: 1, fontSize: 14, color: '#333', textAlign: 'right' },
  buttonGroup: { flexDirection: 'row', justifyContent: 'center', marginTop: 20 },
  button: {
    backgroundColor: 'red',
    paddingVertical: 12,
    paddingHorizontal: 24,
    borderRadius: 6,
    marginHorizontal: 10,
  },
  submitText: { color: '#fff', fontWeight: 'bold' },
  editButton: { backgroundColor: '#ccc' },
  editText: { color: '#000', fontWeight: 'bold' },
});
