import { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';

export default function HasilPositif() {
  const location = useLocation();
  const navigate = useNavigate();

  const {
    nama = 'Nama Tidak Diketahui',
    hasil = 'Positif',
    probabilitas = 0.0,
    identitas,
    gejala,
    tanggal,
  } = location.state || {};

  const [submitStatus, setSubmitStatus] = useState('pending');
  const [hasSubmitted, setHasSubmitted] = useState(false);

  const tanggalObj = tanggal ? new Date(tanggal) : new Date();
  const tanggalScreening = `${tanggalObj.getFullYear()}-${(tanggalObj.getMonth() + 1)
    .toString()
    .padStart(2, '0')}-${tanggalObj.getDate().toString().padStart(2, '0')}`;

  const tanggalFormatted = tanggalObj.toLocaleDateString('id-ID', {
    day: '2-digit',
    month: 'long',
    year: 'numeric',
  });

  useEffect(() => {
    const handleBeforeUnload = (e) => {
      e.preventDefault();
      e.returnValue = '';
    };
    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => window.removeEventListener('beforeunload', handleBeforeUnload);
  }, []);

  useEffect(() => {
    const simpanData = async () => {
      if (!identitas || !gejala) {
        console.warn('⚠️ Data identitas atau gejala tidak tersedia.');
        return;
      }

      const screeningKey = `submitted-${identitas.nik}-${tanggalScreening}`;
      if (sessionStorage.getItem(screeningKey) === 'true') {
        console.log('⛔ Data screening sudah dikirim sebelumnya.');
        return;
      }

      try {
        const userRaw = localStorage.getItem('user');
        const user = JSON.parse(userRaw);

        if (!user || !user.user_id) {
          alert('Anda belum login. Silakan login terlebih dahulu.');
          navigate('/login');
          return;
        }

        const response = await fetch('http://localhost:5000/submit-screening', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            user_id: parseInt(user.user_id),
            nama_pasien: identitas.nama,
            nik: identitas.nik,
            jenis_kelamin: identitas.jenisKelamin,
            no_hp: identitas.telepon,
            usia: identitas.usia,
            alamat: identitas.alamat,
            hasil: hasil,
            catatan: '',
            probabilitas: parseFloat(probabilitas),
            gejala,
            tanggal_screening: tanggalScreening,
          }),
        });

        if (response.status === 409) {
          console.warn('⛔ Data screening sudah ada di database.');
          sessionStorage.setItem(screeningKey, 'true');
          setSubmitStatus('duplicate');
          return;
        }

        if (!response.ok) throw new Error('Gagal menyimpan data ke backend.');

        sessionStorage.setItem(screeningKey, 'true');
        setSubmitStatus('success');
        console.log('✅ Data screening berhasil disimpan.');
      } catch (error) {
        console.error('🔥 Gagal menyimpan data:', error);
        setSubmitStatus('error');
      }
    };

    if (submitStatus === 'pending' && !hasSubmitted) {
      setHasSubmitted(true);
      simpanData();
    }
  }, [
    submitStatus,
    hasSubmitted,
    identitas,
    gejala,
    hasil,
    probabilitas,
    tanggalScreening,
    navigate,
  ]);

  const handleKembali = () => {
    navigate('/mainmenu');
  };

  return (
    <div className="d-flex flex-column min-vh-100">
      <Header />

      <div className="container my-5 flex-grow-1 text-center">
        <h2 className="fw-bold text-danger">SCREENING TBC</h2>
        <p className="text-muted">Berdasarkan hasil screening, Anda terduga TBC.</p>

        <div
          className="card mx-auto p-4"
          style={{ maxWidth: '500px', backgroundColor: '#fdecea' }}
        >
          <img
            src="/assets/images/Sakit.png"
            alt="Sakit"
            className="img-fluid mb-3"
            style={{ maxWidth: 140 }}
          />

          <div className="bg-white shadow-sm rounded p-3 mb-3">
            <h5 className="mb-1 fw-bold">{nama}</h5>
            <small className="text-muted">{tanggalFormatted}</small>
          </div>

          <h5 className="text-danger fw-bold mb-2">Anda Terduga TBC</h5>
          <p className="mb-0">
            Probabilitas: {(parseFloat(probabilitas) * 100).toFixed(2)}%
          </p>

          {submitStatus === 'error' && (
            <div className="alert alert-danger mt-3">
              Gagal menyimpan data screening. Silakan coba lagi nanti.
            </div>
          )}
          {submitStatus === 'duplicate' && (
            <div className="alert alert-warning mt-3">
              Data screening Anda sudah tersimpan sebelumnya.
            </div>
          )}
        </div>

        <p className="mt-4 text-muted">
          Segera lakukan pemeriksaan lebih lanjut ke fasilitas kesehatan terdekat.
        </p>

        <div className="d-grid gap-3 mt-4 col-10 col-md-6 mx-auto">
          <button className="btn btn-outline-secondary" onClick={handleKembali}>
            Kembali ke Halaman Awal
          </button>
        </div>
      </div>

      <Footer />
    </div>
  );
}
