"use client";
import { useEffect, useState } from "react";

const DataScreening = () => {
  const [data, setData] = useState([]);

  // Ambil data dari backend
  const fetchScreeningData = async () => {
    try {
      const response = await fetch("http://localhost:5000/screening");
      const result = await response.json();
      setData(result);
    } catch (error) {
      console.error("Gagal mengambil data:", error);
    }
  };

  // Jalankan saat komponen pertama kali dimuat
  useEffect(() => {
    fetchScreeningData();
  }, []);

  // Fungsi untuk menghapus data
  const handleDelete = async (index) => {
    const confirmDelete = window.confirm("Yakin ingin menghapus data ini?");
    if (!confirmDelete) return;

    const deletedItem = data[index];

    try {
      const response = await fetch(
        `http://localhost:5000/hapus-screening/${deletedItem.id}`,
        {
          method: "DELETE",
        }
      );

      const result = await response.json();

      if (response.ok) {
        // Hapus dari state lokal
        const updatedData = [...data];
        updatedData.splice(index, 1);
        setData(updatedData);

        alert("✅ Data berhasil dihapus dari database.");
      } else {
        alert(`❌ Gagal menghapus: ${result.message}`);
      }
    } catch (error) {
      console.error("Gagal menghapus data:", error);
      alert("❌ Terjadi kesalahan saat menghapus data.");
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">Data Screening TBC</h1>
      <table className="table-auto w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="border px-4 py-2">No</th>
            <th className="border px-4 py-2">Nama</th>
            <th className="border px-4 py-2">Jenis Kelamin</th>
            <th className="border px-4 py-2">Usia</th>
            <th className="border px-4 py-2">Tanggal Screening</th>
            <th className="border px-4 py-2">Status</th>
            <th className="border px-4 py-2">Aksi</th>
          </tr>
        </thead>
        <tbody>
          {data.length > 0 ? (
            data.map((item, index) => (
              <tr key={index} className="text-center">
                <td className="border px-4 py-2">{index + 1}</td>
                <td className="border px-4 py-2">{item.nama}</td>
                <td className="border px-4 py-2">{item.jenis_kelamin}</td>
                <td className="border px-4 py-2">{item.usia}</td>
                <td className="border px-4 py-2">
                  {new Date(item.tanggal_screening).toLocaleDateString("id-ID")}
                </td>
                <td className="border px-4 py-2">{item.status}</td>
                <td className="border px-4 py-2">
                  <button
                    onClick={() => handleDelete(index)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded"
                  >
                    Hapus
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7" className="text-center py-4">
                Tidak ada data.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default DataScreening;
