import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';

export default function DetailScreening() {
  const location = useLocation();
  const navigate = useNavigate();
  const { item } = location.state || {};
  const [allowed, setAllowed] = useState(false);
  const [checking, setChecking] = useState(true);

  const pertanyaanList = [
    'Batuk Lebih Dari 2 Minggu',
    'Demam',
    'Berkeringat Malam Hari Tanpa Aktivitas',
    'Sesak Nafas',
    'Nyeri Dada',
    'Ada benjolan di leher/bawah rahang/bawah telinga/ketiak',
    'Batuk berdarah',
    'Batuk kurang dari 2 minggu',
    'Nafsu makan turun',
    'Mudah lelah',
    'Berat badan turun',
    'Anggota keluarga serumah ada yang sakit TBC',
    'Pernah berada satu ruangan dengan penderita TBC',
    'Tinggal serumah dengan penderita TBC minimal satu malam/sering siang hari',
    'Pernah Berobat TBC tuntas',
    'Pernah berobat TBC tapi tidak tuntas',
    'Punya riwayat diabetes melitus/kencing manis',
    'Orang dengan HIV',
    'Ibu Hamil',
    'Merokok',
    'Usia 0-14 Tahun',
    'Lansia (Diatas 60 Tahun)',
  ];

  const convertJawaban = (val) => {
    if (val === 'ya' || val === 1 || val === '1') return 'Ya';
    if (val === 'tidak' || val === 0 || val === '0') return 'Tidak';
    return 'Tidak tersedia';
  };

  const jawabanArray = (() => {
    if (Array.isArray(item?.jawaban)) return item.jawaban;
    if (typeof item?.jawaban === 'string') {
      try {
        const parsed = JSON.parse(item.jawaban);
        return Array.isArray(parsed)
          ? parsed
          : Object.values(parsed);
      } catch {
        return [];
      }
    }
    if (typeof item?.jawaban === 'object' && item.jawaban !== null) {
      return Object.values(item.jawaban);
    }
    return [];
  })();

  console.log('üß™ item.jawaban:', item?.jawaban);
  console.log('üìä jawabanArray:', jawabanArray);
  console.log('üìã Jumlah jawaban:', jawabanArray.length);

  const parsedProbabilitas = parseFloat(item?.probabilitas);

  useEffect(() => {
    const currentUser = JSON.parse(localStorage.getItem('user'));
    if (
      currentUser?.role === 'admin' ||
      currentUser?.user_id === item?.user_id
    ) {
      setAllowed(true);
    }
    setChecking(false);
  }, [item?.user_id]);

  if (checking) {
    return (
      <div className="container text-center mt-5">
        <h5>Mengecek izin akses...</h5>
      </div>
    );
  }

  if (!allowed) {
    return (
      <div className="container text-center mt-5">
        <div className="alert alert-danger">
          ‚ùå Anda tidak memiliki izin untuk melihat detail screening ini.
        </div>
        <button className="btn btn-secondary" onClick={() => navigate(-1)}>
          üîô Kembali
        </button>
      </div>
    );
  }

  return (
    <div className="d-flex flex-column min-vh-100">
      <Header />
      <div className="container my-4 flex-grow-1">
        <h2 className="mb-4">üìù Detail Screening</h2>

        <p><strong>üë§ Nama:</strong> {item?.nama_pasien}</p>
        <p>
          <strong>üìÖ Tanggal:</strong>{' '}
          {item?.tanggal_screening
            ? new Date(item.tanggal_screening).toLocaleDateString('id-ID', {
                day: '2-digit',
                month: 'long',
                year: 'numeric',
              })
            : 'Tidak tersedia'}
        </p>

        {parsedProbabilitas >= 0 && !isNaN(parsedProbabilitas) && (
          <p><strong>üìà Probabilitas:</strong> {(parsedProbabilitas * 100).toFixed(2)}%</p>
        )}

        {item?.hasil && (
          <p>
            <strong>‚ö†Ô∏è Hasil:</strong>{' '}
            <span className={`fw-bold ${item.hasil.toLowerCase().includes('positif') ? 'text-danger' : 'text-success'}`}>
              {item.hasil}
            </span>
          </p>
        )}

        {jawabanArray?.length > 0 && (
          <>
            <h5 className="mt-4">üóíÔ∏è Jawaban:</h5>
            <div className="list-group">
              {pertanyaanList.map((pertanyaan, index) => {
                const jawabanTeks = convertJawaban(jawabanArray[index]);
                let badgeClass = 'bg-secondary';
                if (jawabanTeks === 'Ya') badgeClass = 'bg-success';
                else if (jawabanTeks === 'Tidak') badgeClass = 'bg-danger';

                return (
                  <div
                    key={index}
                    className="list-group-item d-flex justify-content-between align-items-center"
                  >
                    <span>{index + 1}. {pertanyaan}</span>
                    <span className={`badge rounded-pill ${badgeClass}`}>
                      {jawabanTeks}
                    </span>
                  </div>
                );
              })}
            </div>
          </>
        )}

        <div className="text-center">
          <button className="btn btn-primary mt-4" onClick={() => navigate(-1)}>
            üîô Kembali
          </button>
        </div>
      </div>
      <Footer />
    </div>
  );
}
