import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Footer from '../components/Footer';
import Header from '../components/Header';

export default function MainMenu() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUsername = localStorage.getItem('username');
    const storedRole = localStorage.getItem('role');
    const storedUserId = localStorage.getItem('user_id');

    if (storedUsername && storedUserId) {
      setUser({
        username: storedUsername,
        role: storedRole || 'user',
        user_id: storedUserId,
      });
    } else {
      setUser(null);
    }
  }, []);

  const handleScreening = () => {
    if (!user) {
      navigate('/login', {
        state: {
          redirectTo: '/informasi-awal',
        },
      });
    } else {
      navigate('/informasi-awal');
    }
  };

  const handleCekData = () => {
    navigate('/riwayat');
  };

  const handleKelolaUser = () => {
    navigate('/kelola-user');
  };

  const handleLogout = () => {
    localStorage.clear();
    setUser(null);
    navigate('/');
  };

  return (
    <div className="mainmenu">
      <Header title="Menu Utama" showLogin={!user} />

      <div className="container">
        <h1 className="title">Selamat Datang di Aplikasi Deteksi TBC</h1>
        {user && <p className="username">Login sebagai: {user.username}</p>}
        <p className="subtitle">
          Silakan pilih salah satu opsi di bawah ini untuk melanjutkan.
        </p>

        <div className="card" onClick={handleScreening}>
          <img src="/assets/images/screening.png" alt="Screening" className="card-img" />
          <h2 className="card-title">Screening TBC</h2>
          <p className="card-desc">
            Lakukan pemeriksaan awal untuk mendeteksi potensi TBC.
          </p>
        </div>

        {user && (
          <div className="card" onClick={handleCekData}>
            <img src="/assets/images/history.png" alt="Riwayat" className="card-img" />
            <h2 className="card-title">Riwayat Pemeriksaan</h2>
            <p className="card-desc">Lihat kembali data hasil screening Anda.</p>
          </div>
        )}

        {user?.role === 'admin' && (
          <div className="card" onClick={handleKelolaUser}>
            <img src="/assets/images/admin.png" alt="Admin" className="card-img" />
            <h2 className="card-title">Kelola Pengguna</h2>
            <p className="card-desc">Lihat dan atur akun pengguna lainnya.</p>
          </div>
        )}

        {user && (
          <div className="card logout" onClick={handleLogout}>
            <h2 className="card-title">Keluar</h2>
            <p className="card-desc">Keluar dari aplikasi</p>
          </div>
        )}
      </div>

      <Footer />
    </div>
  );
}
