import AsyncStorage from '@react-native-async-storage/async-storage';
import { useEffect, useState } from 'react';
import {
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from 'react-native';
import Footer from '../components/Footer';
import Header from '../components/Header';

export default function DetailScreening({ route, navigation }) {
  const { item } = route.params;
  const [allowed, setAllowed] = useState(false);
  const [checking, setChecking] = useState(true);

  const pertanyaanList = [
    'Batuk Lebih Dari 2 Minggu',
    'Demam',
    'Berkeringat Malam Hari Tanpa Aktivitas',
    'Sesak Nafas',
    'Nyeri Dada',
    'Ada benjolan di leher/bawah rahang/bawah telinga/ketiak',
    'Batuk berdarah',
    'Batuk kurang dari 2 minggu',
    'Nafsu makan turun (Atau Hilang Nafsu Makan Selama Berhari-hari)',
    'Mudah lelah (Atau Sering Kecapekan Tanpa Aktivitas Fisik Yang Berarti)',
    'Berat badan turun (Turun Drastis Selama Berhari-hari Bukan Karena Diet)',
    'Anggota keluarga serumah ada yang sakit TBC',
    'Pernah berada satu ruangan dengan penderita TBC',
    'Tinggal serumah dengan penderita TBC minimal satu malam/sering siang hari',
    'Pernah Berobat TBC tuntas',
    'Pernah berobat TBC tapi tidak tuntas',
    'Punya riwayat diabetes melitus/kencing manis',
    'Orang dengan HIV',
    'Ibu Hamil',
    'Merokok',
    'Usia 0-14 Tahun',
    'Lansia (Diatas 60 Tahun)',
  ];

  const jawabanArray = Array.isArray(item.jawaban)
    ? item.jawaban
    : Object.values(item.jawaban || {}).map(j => {
        const parts = j.split(':');
        return parts[parts.length - 1].trim();
      });

  useEffect(() => {
    const checkAccess = async () => {
      const currentUser = await AsyncStorage.getItem('user');
      const parsedUser = currentUser ? JSON.parse(currentUser) : null;

      if (
        parsedUser?.role === 'admin' ||
        parsedUser?.user_id === item.user_id
      ) {
        setAllowed(true);
      }
      setChecking(false);
    };

    checkAccess();
  }, []);

  if (checking) {
    return (
      <SafeAreaView style={styles.centered}>
        <Text>Mengecek izin akses...</Text>
      </SafeAreaView>
    );
  }

  if (!allowed) {
    return (
      <SafeAreaView style={styles.centered}>
        <Text style={{ fontSize: 16, color: 'red', textAlign: 'center' }}>
          Anda tidak memiliki izin untuk melihat detail screening ini.
        </Text>
        <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
          <Text style={styles.backButtonText}>Kembali</Text>
        </TouchableOpacity>
      </SafeAreaView>
    );
  }

  return (
    <SafeAreaView style={{ flex: 1, backgroundColor: '#fff' }}>
      <Header />
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.contentWrapper}>
          <Text style={styles.title}>📝 Detail Screening</Text>
          <Text style={styles.label}>
            👤 Nama: <Text style={styles.value}>{item.nama_pasien}</Text>
          </Text>
          <Text style={styles.label}>
            📅 Tanggal:{' '}
            <Text style={styles.value}>
              {item.tanggal_screening
                ? new Date(item.tanggal_screening).toLocaleDateString('id-ID', {
                    day: '2-digit',
                    month: 'long',
                    year: 'numeric',
                  })
                : 'Tidak tersedia'}
            </Text>
          </Text>

          {item.probabilitas != null && (
            <Text style={styles.label}>
              📈 Probabilitas:{' '}
              <Text style={styles.value}>
                {(item.probabilitas * 100).toFixed(2)}%
              </Text>
            </Text>
          )}

          {item.hasil && (
            <Text style={[styles.label, styles.hasil]}>
              ⚠️ Hasil:
              <Text
                style={
                  item.hasil.toLowerCase().includes('positif')
                    ? styles.positif
                    : styles.negatif
                }
              >
                {' ' + item.hasil}
              </Text>
            </Text>
          )}

          {jawabanArray?.length > 0 && (
            <>
              <Text style={styles.subtitle}>🗒️ Jawaban:</Text>
              <View style={styles.answerContainer}>
                {pertanyaanList.map((pertanyaan, index) => (
                  <View key={index} style={styles.answerCard}>
                    <Text style={styles.question}>
                      {index + 1}. {pertanyaan}
                    </Text>
                    <Text
                      style={[
                        styles.jawaban,
                        jawabanArray[index]?.toLowerCase() === 'ya'
                          ? styles.jawabanYa
                          : styles.jawabanTidak,
                      ]}
                    >
                      {jawabanArray[index] ?? 'Tidak tersedia'}
                    </Text>
                  </View>
                ))}
              </View>
            </>
          )}

          <TouchableOpacity style={styles.backButton} onPress={() => navigation.goBack()}>
            <Text style={styles.backButtonText}>🔙 Kembali</Text>
          </TouchableOpacity>
        </View>
        <Footer />
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  scrollContent: { flexGrow: 1, justifyContent: 'space-between' },
  contentWrapper: { padding: 16 },
  title: { fontSize: 24, fontWeight: 'bold', marginBottom: 16, color: '#333' },
  label: { fontSize: 16, marginBottom: 8, color: '#555' },
  value: { fontWeight: '600', color: '#222' },
  hasil: { marginTop: 10, fontSize: 18 },
  positif: { color: '#c62828', fontWeight: 'bold' },
  negatif: { color: '#2e7d32', fontWeight: 'bold' },
  subtitle: { fontSize: 20, marginTop: 20, marginBottom: 12, fontWeight: 'bold', color: '#444' },
  answerContainer: { gap: 8 },
  answerCard: {
    backgroundColor: '#f7f7f7',
    padding: 12,
    borderRadius: 8,
    borderLeftWidth: 4,
    borderLeftColor: '#ddd',
  },
  question: { fontSize: 14, color: '#333' },
  jawaban: { marginTop: 4, fontSize: 14, fontWeight: 'bold' },
  jawabanYa: { color: '#2e7d32' },
  jawabanTidak: { color: '#c62828' },
  backButton: {
    marginTop: 24,
    backgroundColor: '#1976d2',
    paddingVertical: 12,
    borderRadius: 6,
    alignItems: 'center',
  },
  backButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
  centered: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
});
