import AsyncStorage from '@react-native-async-storage/async-storage';
import { useEffect, useState } from 'react';
import {
  Image,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from 'react-native';
import Footer from '../components/Footer';
import Header from '../components/Header';

export default function MainMenu({ navigation }) {
  const [user, setUser] = useState(null); // user: { username, role, user_id }

  useEffect(() => {
    const fetchUserData = async () => {
      const storedUsername = await AsyncStorage.getItem('username');
      const storedRole = await AsyncStorage.getItem('role');
      const storedUserId = await AsyncStorage.getItem('user_id');

      if (storedUsername && storedUserId) {
        setUser({
          username: storedUsername,
          role: storedRole || 'user',
          user_id: storedUserId,
        });
      } else {
        setUser(null);
      }
    };

    const unsubscribe = navigation.addListener('focus', fetchUserData);
    return unsubscribe;
  }, [navigation]);

  const handleScreening = () => {
    if (!user) {
      // Redirect ke login dengan informasi tujuan setelah login
      navigation.navigate('LoginScreen', {
        redirectTo: 'InformasiAwal',
        params: {},
      });
      return;
    }

    // Jika sudah login, lanjut ke screening
    navigation.navigate('InformasiAwal');
  };

  const handleCekData = () => {
    navigation.navigate('DataScreening');
  };

  const handleKelolaUser = () => {
    navigation.navigate('KelolaUser');
  };

  const handleLogout = async () => {
    await AsyncStorage.clear();
    setUser(null);
    navigation.replace('MainMenu');
  };

  return (
    <View style={{ flex: 1, backgroundColor: '#fff' }}>
      <Header title="Menu Utama" showLogin={!user} navigation={navigation} />

      <ScrollView
        contentContainerStyle={{
          flexGrow: 1,
          justifyContent: 'space-between',
        }}
      >
        <View style={styles.container}>
          <Text style={styles.title}>Selamat Datang di Aplikasi Deteksi TBC</Text>
          {user && (
            <Text style={styles.username}>Login sebagai: {user.username}</Text>
          )}
          <Text style={styles.subtitle}>
            Silakan pilih salah satu opsi di bawah ini untuk melanjutkan.
          </Text>

          <TouchableOpacity style={styles.card} onPress={handleScreening}>
            <Image
              source={require('../assets/images/screening.png')}
              style={styles.cardImage}
              resizeMode="contain"
            />
            <Text style={styles.cardTitle}>Screening TBC</Text>
            <Text style={styles.cardDesc}>
              Lakukan pemeriksaan awal untuk mendeteksi potensi TBC.
            </Text>
          </TouchableOpacity>

          {/* Tampilkan riwayat jika login */}
          {user && (
            <TouchableOpacity style={styles.card} onPress={handleCekData}>
              <Image
                source={require('../assets/images/history.png')}
                style={styles.cardImage}
                resizeMode="contain"
              />
              <Text style={styles.cardTitle}>Riwayat Pemeriksaan</Text>
              <Text style={styles.cardDesc}>
                Lihat kembali data hasil screening Anda.
              </Text>
            </TouchableOpacity>
          )}

          {/* Tampil hanya untuk admin */}
          {user?.role === 'admin' && (
            <TouchableOpacity style={styles.card} onPress={handleKelolaUser}>
              <Image
                source={require('../assets/images/admin.png')}
                style={styles.cardImage}
                resizeMode="contain"
              />
              <Text style={styles.cardTitle}>Kelola Pengguna</Text>
              <Text style={styles.cardDesc}>
                Lihat dan atur akun pengguna lainnya.
              </Text>
            </TouchableOpacity>
          )}

          {/* Tombol logout */}
          {user && (
            <TouchableOpacity
              style={[styles.card, { backgroundColor: '#f8d7da' }]}
              onPress={handleLogout}
            >
              <Text style={[styles.cardTitle, { color: '#b71c1c' }]}>Keluar</Text>
              <Text style={styles.cardDesc}>Keluar dari aplikasi</Text>
            </TouchableOpacity>
          )}
        </View>

        <Footer />
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    padding: 20,
    alignItems: 'center',
  },
  title: {
    fontSize: 22,
    fontWeight: 'bold',
    marginBottom: 8,
    color: '#333',
    textAlign: 'center',
  },
  username: {
    fontSize: 16,
    color: '#555',
    marginBottom: 6,
  },
  subtitle: {
    fontSize: 14,
    textAlign: 'center',
    color: '#666',
    marginBottom: 24,
  },
  card: {
    backgroundColor: '#bcbcbc',
    borderRadius: 12,
    padding: 20,
    width: '100%',
    alignItems: 'center',
    marginBottom: 20,
    elevation: 3,
  },
  cardImage: {
    width: 120,
    height: 120,
    marginBottom: 16,
  },
  cardTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#C62828',
    marginBottom: 8,
  },
  cardDesc: {
    fontSize: 14,
    color: '#444',
    textAlign: 'center',
  },
});
