import AsyncStorage from '@react-native-async-storage/async-storage';
import { useEffect, useState } from 'react';
import {
  Alert,
  Platform,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from 'react-native';
import Footer from '../components/Footer';
import Header from '../components/Header';
import { allQuestions, informasiLainnyaQuestions, keluhanQuestions } from './question';

export default function Screening({ navigation, route }) {
  const { identitas } = route.params || {};
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkLogin = async () => {
      const userId = await AsyncStorage.getItem('user_id');
      if (!userId) {
        Alert.alert(
          'Akses Ditolak',
          'Silakan login terlebih dahulu sebelum melakukan screening.',
          [
            {
              text: 'OK',
              onPress: () => navigation.replace('LoginScreen', {
                redirectTo: 'IdentitasAwal',
                params: { namaLengkap: identitas?.nama, nomorNIK: identitas?.nik },
              }),
            },
          ]
        );
      } else {
        setLoading(false);
      }
    };
    checkLogin();
  }, []);

  const toggleAnswer = (question, value) => {
    setAnswers((prev) => ({
      ...prev,
      [question]: value,
    }));
  };

  const handleNext = () => {
    if (Object.keys(answers).length < allQuestions.length) {
      const unanswered = allQuestions.find((q) => !(q in answers));
      const message = `Pertanyaan "${unanswered}" belum dijawab.`;
      if (Platform.OS === 'web') {
        window.alert(message);
      } else {
        Alert.alert('Validasi Gagal', message);
      }
      return;
    }

    navigation.navigate('ReviewJawaban', {
      identitas,
      screeningAnswers: answers,
    });
  };

  if (loading) return null;

  return (
    <SafeAreaView style={styles.safeArea}>
      <Header />
      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.formContainer}>
          <Text style={styles.title}>SCREENING TBC</Text>
          <Text style={styles.subtitle}>
            TBC itu bisa diobati kok, Telaten minum obat dan rutin kontrol. Jangan takut untuk memeriksakan diri!
          </Text>

          <View style={styles.card}>
            <Text style={styles.sectionTitle}>Keluhan Yang Dirasakan</Text>
            {keluhanQuestions.map((question, index) => (
              <View key={index} style={styles.questionRow}>
                <Text style={styles.questionText}>{index + 1}. {question}</Text>
                <View style={styles.toggleContainer}>
                  <TouchableOpacity
                    style={[
                      styles.toggleButton,
                      answers[question] === 'Ya' && styles.selectedToggle,
                    ]}
                    onPress={() => toggleAnswer(question, 'Ya')}
                  >
                    <Text style={answers[question] === 'Ya' ? styles.selectedText : styles.toggleText}>Ya</Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={[
                      styles.toggleButton,
                      answers[question] === 'Tidak' && styles.selectedToggle,
                    ]}
                    onPress={() => toggleAnswer(question, 'Tidak')}
                  >
                    <Text style={answers[question] === 'Tidak' ? styles.selectedText : styles.toggleText}>Tidak</Text>
                  </TouchableOpacity>
                </View>
              </View>
            ))}

            <Text style={[styles.sectionTitle, { marginTop: 24 }]}>Informasi Lainnya</Text>
            {informasiLainnyaQuestions.map((question, index) => (
              <View key={keluhanQuestions.length + index} style={styles.questionRow}>
                <Text style={styles.questionText}>
                  {keluhanQuestions.length + index + 1}. {question}
                </Text>
                <View style={styles.toggleContainer}>
                  <TouchableOpacity
                    style={[
                      styles.toggleButton,
                      answers[question] === 'Ya' && styles.selectedToggle,
                    ]}
                    onPress={() => toggleAnswer(question, 'Ya')}
                  >
                    <Text style={answers[question] === 'Ya' ? styles.selectedText : styles.toggleText}>Ya</Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={[
                      styles.toggleButton,
                      answers[question] === 'Tidak' && styles.selectedToggle,
                    ]}
                    onPress={() => toggleAnswer(question, 'Tidak')}
                  >
                    <Text style={answers[question] === 'Tidak' ? styles.selectedText : styles.toggleText}>Tidak</Text>
                  </TouchableOpacity>
                </View>
              </View>
            ))}

            <View style={styles.buttonGroup}>
              <TouchableOpacity
                style={[styles.button, styles.secondaryButton]}
                onPress={() => navigation.goBack()}
              >
                <Text style={styles.secondaryButtonText}>Sebelumnya</Text>
              </TouchableOpacity>
              <TouchableOpacity style={styles.button} onPress={handleNext}>
                <Text style={styles.buttonText}>Review</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
        <Footer />
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  safeArea: {
    flex: 1,
    backgroundColor: '#fff',
  },
  scrollContent: {
    flexGrow: 1,
    justifyContent: 'space-between',
    minHeight: '100%',
  },
  formContainer: {
    padding: 20,
  },
  title: {
    fontSize: 22,
    fontWeight: 'bold',
    color: 'red',
    textAlign: 'center',
    marginBottom: 8,
  },
  subtitle: {
    fontSize: 14,
    textAlign: 'center',
    marginBottom: 20,
  },
  card: {
    borderColor: 'red',
    borderWidth: 2,
    borderRadius: 8,
    padding: 20,
    backgroundColor: '#fdfdfd',
  },
  sectionTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'red',
    marginBottom: 16,
  },
  questionRow: {
    marginBottom: 16,
  },
  questionText: {
    marginBottom: 8,
    fontSize: 14,
  },
  toggleContainer: {
    flexDirection: 'row',
    justifyContent: 'flex-start',
  },
  toggleButton: {
    padding: 10,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 6,
    marginRight: 10,
    minWidth: 60,
    alignItems: 'center',
  },
  selectedToggle: {
    borderColor: 'red',
    backgroundColor: '#ffe6e6',
  },
  toggleText: {
    color: '#555',
  },
  selectedText: {
    color: 'red',
    fontWeight: 'bold',
  },
  buttonGroup: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 20,
  },
  button: {
    backgroundColor: 'red',
    padding: 14,
    borderRadius: 6,
    flex: 1,
    marginHorizontal: 4,
    alignItems: 'center',
  },
  secondaryButton: {
    backgroundColor: '#eee',
  },
  secondaryButtonText: {
    color: '#333',
    fontWeight: 'bold',
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
  },
});
